import React, { useState } from 'react';

function DictionaryApp() {
  const [words, setWords] = useState([]);
  const [newWord, setNewWord] = useState('');
  const [definitions, setDefinitions] = useState({});

  const handleInputChange = (event) => {
    setNewWord(event.target.value);
  };

  const addWord = () => {
    if (newWord.trim() !== '') {
      setWords([...words, newWord]);
      setNewWord('');
    }
  };

  const removeWord = (word) => {
    const updatedWords = words.filter((w) => w !== word);
    const updatedDefinitions = { ...definitions };
    delete updatedDefinitions[word];

    setWords(updatedWords);
    setDefinitions(updatedDefinitions);
  };

  const handleDefinitionChange = (event, word) => {
    const updatedDefinitions = { ...definitions };
    updatedDefinitions[word] = event.target.value;
    setDefinitions(updatedDefinitions);
  };

  return (
    <div>
      <h1>Słownik</h1>
      <div>
        <input
          type="text"
          value={newWord}
          onChange={handleInputChange}
          placeholder="Wprowadź nowe słowo"
        />
        <button onClick={addWord}>Dodaj słowo</button>
      </div>
      <ul>
        {words.map((word) => (
          <li key={word}>
            {word}
            <input
              type="text"
              value={definitions[word] || ''}
              onChange={(event) => handleDefinitionChange(event, word)}
              placeholder="Wprowadź definicję"
            />
            <button onClick={() => removeWord(word)}>Usuń</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default DictionaryApp;
