import React, { useState, useEffect } from 'react';

function App() {
  const [slowa, setSlowa] = useState([]);
  const [wpis, setWpis] = useState({ slowo: '', definicja: '' });
  const [wybraneSlowo, setWybraneSlowo] = useState(null);

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setWpis((prevWpis) => ({ ...prevWpis, [name]: value}));
  };

  const dodajWpis = () => {
    if (wpis.slowo.trim() !== '') {
      setSlowa([...slowa, wpis]);
      setWpis({ slowo: '', definicja: '' });
    }
  };

  const usunWpis = (wpis) => {
    const noweSlowa = slowa.filter((s) => s !== wpis);
    setSlowa(noweSlowa);
  };

  const pokazDefinicje = (wpis) => {
    setWybraneSlowo(wpis);
  };

  useEffect(() => {
    fetch('https://api.dictionaryapi.dev/api/v2/entries/en/random')
      .then((response) => response.json())
      .then((data) => {
        if (Array.isArray(data)) {
          const wordsList = data.map((entry) => entry.word);
          const randomIndex = Math.floor(Math.random() * wordsList.length);
          const selectedWord = wordsList[randomIndex];
          setWybraneSlowo(selectedWord);
        }
      })
      .catch((error) => console.log(error));
  }, []);

  return (
    <div>
      <h1>Słownik</h1>
      <div>
        <input
          type="text"
          name="slowo"
          value={wpis.slowo}
          onChange={handleInputChange}
          placeholder="Wprowadź nowe słowo"
        />
        <input
          type="text"
          name="definicja"
          value={wpis.definicja}
          onChange={handleInputChange}
          placeholder="Wprowadź definicję"
        />
        <button onClick={dodajWpis}>Dodaj słowo</button>
      </div>
      <table>
        <thead>
          <th>
            Słowa
          </th>
          <th>
            Pokaż
          </th>
        </thead>
        <tbody>
        {slowa.map((wpis, index) => (
          <tr>
            <td key={index}>
            {wpis.slowo}
            </td>
            <td>              
            <button onClick={() => pokazDefinicje(wpis)}>Pokaż definicję</button>
            </td>
            <button onClick={() => usunWpis(wpis)}>Usuń</button>
            </tr>
        ))}
        </tbody>
      </table>

    <div>
      <h1>Losowe słowo</h1>
      {randomWord && <h2>{randomWord}</h2>}
    </div>
      {wybraneSlowo && (
        <div>
          <h2>Definicja słowa {wybraneSlowo.slowo}</h2>
          <p>{wybraneSlowo.definicja}</p>
        </div>
      )}
    </div>
  );
}

export default App;

