import React, { useState } from 'react';

function App() {
  const [slowa, setSlowa] = useState([]);
  const [wpis, setWpis] = useState({ slowo: '', definicja: '' });

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setWpis((prevWpis) => ({ ...prevWpis, [name]: value}));
  };

  const dodajWpis = () => {
    if (wpis.slowo.trim() !== '') {
      setSlowa([...slowa, wpis]);
      setWpis({ slowo: '', definicja: '' });
    }
  };

  const removeWord = (wpis) => {
    const noweSlowa = slowa.filter((s) => s !== wpis);
    setSlowa(noweSlowa);
  };


  return (
    <div>
      <h1>Słownik</h1>
      <div>
        <input
          type="text"
          value={wpis.slowo}
          onChange={handleInputChange}
          placeholder="Wprowadź nowe słowo"
        />
        <input
          type="text"
          value={wpis.definicja}
          onChange={handleInputChange}
          placeholder="Wprowadź definicję"
        />
        <button onClick={dodajWpis}>Dodaj słowo</button>
      </div>
      <table>
        <thead>
          <th>
            Słowa
          </th>
          <th>
            Definicje
          </th>
        </thead>
        <tbody>
        {slowa.map((wpis) => (
          <tr>
            <td key={wpis}>
            {wpis.slowo}
            </td>
            <td>
                  {wpis.definicja}
            </td>
            <button onClick={() => removeWord(wpis)}>Usuń</button>
            </tr>
        ))}
        </tbody>
      </table>
    </div>
  );
}

export default App;
