import React, { useState } from 'react';

function App() {
  const [words, setWords] = useState([]);
  const [newWord, setNewWord] = useState('');
  const [definitions, setDefinitions] = useState({});

  const handleInputChange = (event) => {
    setNewWord(event.target.value);
  };

  const addWord = async () => {
    if (newWord.trim() !== '') {
      try {
        const response = await fetchDefinition(newWord);
        const definition = response.results[0]?.lexicalEntries[0]?.entries[0]?.senses[0]?.definitions[0];

        setWords([...words, newWord]);
        setDefinitions({ ...definitions, [newWord]: definition });
        setNewWord('');
      } catch (error) {
        console.error('Błąd podczas pobierania definicji:', error);
      }
    }
  };

  const removeWord = (word) => {
    const updatedWords = words.filter((w) => w !== word);
    const updatedDefinitions = { ...definitions };
    delete updatedDefinitions[word];

    setWords(updatedWords);
    setDefinitions(updatedDefinitions);
  };

  const fetchDefinition = async (word) => {
    const appId = 'YOUR_APP_ID';
    const appKey = 'YOUR_APP_KEY';
    const language = 'en';
    const url = `https://od-api.oxforddictionaries.com/api/v2/entries/${language}/${word.toLowerCase()}`;

    const response = await fetch(url, {
      headers: {
        Accept: 'application/json',
        app_id: appId,
        app_key: appKey,
      },
    });

    if (!response.ok) {
      throw new Error('Nie udało się pobrać definicji słowa.');
    }

    const data = await response.json();
    return data;
  };

  return (
    <div>
      <h1>Słownik</h1>
      <div>
        <input
          type="text"
          value={newWord}
          onChange={handleInputChange}
          placeholder="Wprowadź nowe słowo"
        />
        <button onClick={addWord}>Dodaj słowo</button>
      </div>
      <ul>
        {words.map((word) => (
          <li key={word}>
            {word}
            <button onClick={() => removeWord(word)}>Usuń</button>
            {definitions[word] && <p>{definitions[word]}</p>}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
